import 'package:flutter/cupertino.dart';
import 'package:flutter/material.dart';
import 'package:http/http.dart' as http;

import 'home.dart';

class Test extends StatefulWidget {
  const Test({Key? key}) : super(key: key);

  @override
  _TestState createState() => _TestState();
}

class _TestState extends State<Test> {
  var emailController = TextEditingController();
  var passwordController = TextEditingController();

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      body: Padding(
        padding: const EdgeInsets.all(10.0),
        child: SafeArea(
          child: Center(
            child: Column(
              mainAxisAlignment: MainAxisAlignment.center,
              children: [
                  Image.network("https://pbs.twimg.com/profile_images/1383911916188291081/cGRm2J6o_400x400.jpg", width: 200,
                      fit:BoxFit.fill),
                const SizedBox(height: 45,),
                const Text("Ragnar"),
                const SizedBox(height: 45,),
                Image.network("data:image/jpeg;base64,", width: 200,
                    fit:BoxFit.fill),
                const SizedBox(height: 45,),
                const Text("Renaud"),
              ],
            ),
          ),
        ),
      ),
    );
  }

  Future<void> login() async {
    if (emailController.text.isNotEmpty) {
      if (passwordController.text.isNotEmpty) {
        var reponse = await http.post(
            Uri.parse("https://reqres.in/api/login"), body: ({
          'email': emailController.text,
          'password': passwordController.text
        }));
        if (reponse.statusCode == 200) {
          Navigator.push(
              context, MaterialPageRoute(builder: (context) => Home()));
        } else {
          ScaffoldMessenger.of(context).showSnackBar(const SnackBar(
              content: Text(
                  "L'identifiant et/ou le mot de passe est incorrect.")));
        }
      } else {
        ScaffoldMessenger.of(context).showSnackBar(const SnackBar(content: Text("Le mot de passe ne peux être vide."),));
      }
    } else {
      ScaffoldMessenger.of(context).showSnackBar(const SnackBar(content: Text("L'identifiant ne peux être vide."),));
    }
  }
}
